void make_def_file(const char *filename, const char *subject_id, int sample_rate);

int main()
{
    const char *subject_id;
    const int sample_rate = 44100;  // dependent on HRIR data
    int arr_count = sizeof(subject_ids)/sizeof(subject_ids[0]);
    for (int i = 0; i < arr_count; i++) {
        subject_id = subject_ids[i];
        char buf[32] = { 0 };
        snprintf(buf, sizeof(buf), "bin/hrtf_defs/IRC_%s.def", subject_id);
        make_def_file(buf, subject_id, sample_rate);
    }
    for (int i = 0; i < arr_count; i++) {
        subject_id = subject_ids[i];
        char buf[128] = { 0 };
        snprintf(buf, sizeof(buf), "cd bin/hrtf_defs && makehrtf.exe -i IRC_%s.def -o oalsoft_hrtf_%s-%d.mhr", subject_id, subject_id, sample_rate);
        system(buf);
    }
    printf("fin.\n");
    int fix_unused_warning = getchar();
    return 0 * fix_unused_warning;
}

