    fprintf(f, "#\n");
    fprintf(f, "# This definition is used to transform the left and right ear HRIRs of any\n");
    fprintf(f, "# raw data set from the IRCAM/AKG Listen HRTF database.\n");
    fprintf(f, "#\n");
    fprintf(f, "# The data sets are available free of charge from:\n");
    fprintf(f, "#\n");
    fprintf(f, "#  http://recherche.ircam.fr/equipes/salles/listen/index.html\n");
    fprintf(f, "#\n");
    fprintf(f, "# Contact for the Listen HRTF Database:\n");
    fprintf(f, "#\n");
    fprintf(f, "#  Olivier Warusfel <olivier.warusfel@ircam.fr>,\n");
    fprintf(f, "#  Room Acoustics Team, IRCAM\n");
    fprintf(f, "#  1, place Igor Stravinsky\n");
    fprintf(f, "#  75004 PARIS, France\n");
    fprintf(f, "\n");
    fprintf(f, "rate     = %d\n", sample_rate);
    fprintf(f, "\n");
    fprintf(f, "# The IRCAM sets are stereo because they provide both ear HRIRs.\n");
    fprintf(f, "type     = stereo\n");
    fprintf(f, "\n");
